var Roblox = Roblox || {};

Roblox.thumbnails = (function () {
	let thumbnailCache = new TimedCache(15 * 60 * 1000);

	let thumbnailStates = {
		"Error": "Error",
		"Completed": "Completed",
		"InReview": "InReview",
		"Pending": "Pending",
		"Blocked": "Blocked"
	};

	let thumbnailTypes = {
		bundle: "bundle",
		asset: "asset",
		userHeadshot: "userHeadshot"
	};

	let thumbnailStateImages = {
		"Blocked": "data:image/svg+xml;base64,",
		"InReview": "data:image/svg+xml;base64,",
		"Error": "data:image/svg+xml;base64,",
		"Pending": "https://images.rbxcdn.com/c94b4b3bdd1be463ef59dae29f93f882-thumbnail_status_unavailable_dark.svg",
		"Unknown": "data:image/svg+xml;base64,"
	};

	let thumbnailItemToKey = function (thumbnail) {
		return JSON.stringify(thumbnail);
	};

	let getThumbnailForState = function (state) {
		return thumbnailStateImages[state] || thumbnailStateImages.Unknown;
	};

	let processThumbnails = function (thumbnails) {
		return new Promise((resolve, reject) => {
			let requestIds = {};
			let requestData = [];

			thumbnails.forEach((thumbnail, i) => {
				let key = `${thumbnail.type}:${thumbnail.targetId}:${thumbnail.size}:${(+new Date)}:${i}`;
				requestIds[key] = thumbnail;

				requestData.push({
					requestId: key,
					targetId: thumbnail.targetId,
					type: thumbnail.type,
					size: thumbnail.size
				});
			});

			$.post("https://thumbnails.sitetest2.robloxlabs.com/v1/batch", requestData).done((r) => {
				let result = [];

				r.data.forEach((thumbnail) => {
					switch (thumbnail.state) {
						case thumbnailStates.Pending:
							// Retry
							return;
						default:
							let item = requestIds[thumbnail.requestId];
							let cacheKey = thumbnailItemToKey(item);
							let thumbnailResult = {
								state: thumbnail.state,
								imageUrl: thumbnail.imageUrl || getThumbnailForState(thumbnail.state)
							};

							thumbnailCache.set(cacheKey, thumbnailResult);

							result.push({
								success: true,
								item: requestIds[thumbnail.requestId],
								value: thumbnailResult
							});

							return;
					}
				});

				resolve(result);
			}).fail((jxhr, errors) => {
				reject(errors);
			});
		});
	};

	let batchThumbnailProcessor = new BatchItemProcessor({}, processThumbnails, console.error.bind(console, "Roblox.thumbnails"));

	let getThumbnail = function (type, targetId, size) {
		return new Promise(function (resolve, reject) {
			let thumbnailItem = {
				type: type,
				targetId: targetId,
				size: size
			};

			let cacheKey = thumbnailItemToKey(thumbnailItem);
			let cacheItem = thumbnailCache.get(cacheKey);

			if (cacheItem.exists) {
				resolve(cacheItem);
				return;
			}

			batchThumbnailProcessor.push(thumbnailItem).then(resolve).catch(function (error) {
				resolve({
					state: thumbnailStates.Error,
					imageUrl: getThumbnailForState(thumbnailStates.Error)
				});
			});
		});
	};

	/* https://thumbnails.sitetest2.robloxlabs.com/docs/json/v1
	"Roblox.Thumbnails.Apis.Models.ThumbnailBatchRequest": {
		"type": "object",
		"properties": {
			"requestId": {
				"description": "The request id. (Generated client side, used to represent the items in the request)",
				"type": "string"
			},
			"targetId": {
				"format": "int64",
				"description": "The thumbnail target id",
				"type": "integer"
			},
			"type": {
				"description": "The type of the thumbnails",
				"enum": [
					"Avatar",
					"AvatarHeadShot",
					"GameIcon",
					"BadgeIcon",
					"GameThumbnail",
					"GamePass",
					"Asset",
					"BundleThumbnail",
					"Outfit",
					"GroupIcon"
				],
				"type": "string"
			},
			"size": {
				"description": "The thumbnail size",
				"type": "string"
			}
		}
	},
	*/

	let thumbnails = {
		states: thumbnailStates,
		types: thumbnailTypes,

		getThumbnailForState: getThumbnailForState,

		parseSize: function(size) {
			if (typeof(size) === "string") {
				let split = size.split("x");
				let width = Number(split[0]);
				let height = Number(split[1]);

				if (!isNaN(width) && !isNaN(height)) {
					return {
						width: width,
						height: height
					};
				}
			}
		},

		getLargestAvailableThumbnailSize: function(thumbnailType) {
			return new Promise(function(resolve, reject) {
				switch (thumbnailType) {
					case thumbnailTypes.bundle:
					case thumbnailTypes.asset:
						resolve({
							width: 420,
							height: 420
						});
						return;
					case thumbnailTypes.userHeadshot:
						resolve({
							width: 150,
							height: 150
						});
						return;
					default:
						reject({
							code: 7,
							message: `Invalid thumbnailType: ${thumbnailType}`
						});
				}
			});
		},

		getAssetThumbnail: $.promise.cache(function (resolve, reject, assetId, width, height) {
			Roblox.thumbnails.getThumbnail(thumbnailTypes.asset, assetId, width, height).then(resolve).catch(reject);
		}, {
			rejectExpiry: 15 * 1000,
			resolveExpiry: 15 * 60 * 1000
		}),

		getAssetThumbnailUrl: $.promise.cache(function (resolve, reject, assetId, width, height) {
			Roblox.thumbnails.getThumbnailUrl(thumbnailTypes.asset, assetId, width, height).then(resolve).catch(reject);
		}, {
			rejectExpiry: 15 * 1000,
			resolveExpiry: 15 * 60 * 1000
		}),

		getUserHeadshotThumbnail: $.promise.cache(function (resolve, reject, userId, width, height) {
			Roblox.thumbnails.getThumbnail(thumbnailTypes.userHeadshot, userId, width, height).then(resolve).catch(reject);
		}, {
			rejectExpiry: 15 * 1000,
			resolveExpiry: 15 * 60 * 1000
		}),

		getUserHeadshotThumbnailUrl: $.promise.cache(function (resolve, reject, userId, width, height) {
			Roblox.thumbnails.getThumbnailUrl(thumbnailTypes.userHeadshot, userId, width, height).then(resolve).catch(reject);
		}, {
			rejectExpiry: 15 * 1000,
			resolveExpiry: 15 * 60 * 1000
		}),

		getBundleThumbnail: $.promise.cache(function (resolve, reject, bundleId, width, height) {
			Roblox.thumbnails.getThumbnail(thumbnailTypes.bundle, bundleId, width, height).then(resolve).catch(reject);
		}, {
			rejectExpiry: 15 * 1000,
			resolveExpiry: 15 * 60 * 1000
		}),

		getBundleThumbnailUrl: $.promise.cache(function (resolve, reject, bundleId, width, height) {
			Roblox.thumbnails.getThumbnailUrl(thumbnailTypes.bundle, bundleId, width, height).then(resolve).catch(reject);
		}, {
			rejectExpiry: 15 * 1000,
			resolveExpiry: 15 * 60 * 1000
		}),

		getThumbnail: $.promise.cache(function(resolve, reject, thumbnailType, targetId, width, height) {
			let thumbnailApiType;
			switch (thumbnailType) {
				case thumbnailTypes.bundle:
					thumbnailApiType = "BundleThumbnail";
					break;
				case thumbnailTypes.asset:
					thumbnailApiType = "Asset";
					break;
				case thumbnailTypes.userHeadshot:
					thumbnailApiType = "AvatarHeadShot";
					break;
				default:
					reject([
						{
							code: 7,
							message: `Invalid thumbnailType: ${thumbnailType}`
						}
					]);
					return;
			}

			getThumbnail(thumbnailApiType, targetId, `${width}x${height}`).then(resolve).catch(reject);
		}, {
			rejectExpiry: 15 * 1000,
			resolveExpiry: 15 * 60 * 1000
		}),

		getThumbnailUrl: $.promise.cache(function(resolve, reject, thumbnailType, targetId, width, height) {
			Roblox.thumbnails.getThumbnail(thumbnailType, targetId, width, height).then((thumbnail) => {
				resolve(thumbnail.imageUrl);
			}).catch(() => {
				resolve(getThumbnailForState(thumbnailStates.Error));
			});
		}, {
			rejectExpiry: 15 * 1000,
			resolveExpiry: 15 * 60 * 1000
		})
	};

	if (ext.isBackground) {
		thumbnails.thumbnailCache = thumbnailCache;
		thumbnails.thumbnailProcessor = batchThumbnailProcessor;
		thumbnailCache.getThumbnail = getThumbnail;
	}

	return thumbnails;
})();

Roblox.thumbnails = $.addTrigger($.promise.background("Roblox.thumbnails", Roblox.thumbnails));


